<?phpnamespace LVBundle\Service;class Youtube{    private $youtube = null;    private $google_client = null;    private $api_key = null;    /**     * @param $api_key     */    public function __construct($api_key)    {        $this->api_key = $api_key;        $this->google_client = new \Google_Client();        $this->google_client->setDeveloperKey($this->api_key);        $this->youtube = new \Google_Service_YouTube($this->google_client);    }    /**     * @param $keyword     * @param null $token     * @return \Google_Service_YouTube_SearchListResponse     */    public function searchVideo($keyword, $token = null, $maxResult = null)    {        if ($this->youtube != null && !empty($keyword)) {            $filter = array(                'q' => $keyword,                'maxResults' => ($maxResult != null) ? $maxResult : "20",                'type' => "video",            );            if (null != $token) {                $filter["pageToken"] = $token;            } else {                $filter["order"] = "relevance";            }            return $this->youtube->search->listSearch('id,snippet', $filter);        }        return null;    }    public function getVideoYoutube($id)    {        if ($this->youtube != null && !empty($id)) {            $response = $this->youtube->videos->listVideos('id,snippet,contentDetails', ['id' => $id]);            return $response['items'][0];        }        return null;    }    public function date($date, $format)    {        return (new \DateInterval($date))->format($format);    }}